import icqq from 'icqq';
import ScriptBase from '../pluginLoader';

// @ts-ignore
const fetch = (...args: any[]) => import('node-fetch').then(({default: fetch}) => fetch(...args));

export default class extends ScriptBase {
  public constructor() {
    super({
      name: 'Account'
    });
    this.Init();
  };

  private Init() {
    this.client.on('message.group.normal', event => {
      const request = PluginCommon.request(event);
      if (request.IsFail) return;
      if (!request.IsForOwn) return;

      if (request.Command === 'bot_bans' && request.Argument.length == 0) {
        if (!GetBotBannedList(event.reply.bind(event))) {
          event.reply('/bot_bans@$bot_uid 执行失败，具体详见日志输出。');
        };
      };
    });
  };
};

async function GetBotBannedList(reply: (a: icqq.Sendable, b?: boolean) => Promise<any>) {
  const UserId = ProcessContext.client.uin;
  const AppId = 1109907872;
  const Count = 20;
  const Client = ProcessContext.client;

  let LightAppCode: Number;
  { // Get Code From AppId
    let object = {
      1: 3,
      2: Client.apk.qua == '' ? `V1_AND_SQ_${Client.apk.ver}_1234_YYB_D` : Client.apk.qua,
      3: `i=${Client.device.guid.toString('hex')
        }&imsi=&mac=${Client.device.mac_address
        }&m=${Client.device.model
        }&o=0&a=0&sd=0&c64=1&sc=1&p=1080*2221&aid=${Client.device.guid.toString('hex')
        }&f=${Client.device.brand
        }&mm=00&cf=00&cc=00&qimei=&qimei36=&sharpP=1&n=wifi&support_xsj_live=true&client_mod=default`,
      4: {
        1: String(AppId)
      },
      5: String(UserId)
    };

    let payload = await Client.sendUni('LightAppSvc.mini_program_auth.GetCode', icqq.core.pb.encode(object));
    let result = icqq.core.pb.decode(payload);
    LightAppCode = icqq.core.pb.decode(result[4].toBuffer())[2].toString();
  };

  if (!LightAppCode) {
    Logger.Error(`Account@GetBotBannedList -> Get code fail.`);
    return false;
  };

  let QueryUrl = 'https://minico.qq.com/minico/oauth20?uin=QQ%E5%AE%89%E5%85%A8%E4%B8%AD%E5%BF%83';
  let QueryOptions = {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json'
    },
    body: JSON.stringify({
      code: LightAppCode,
      appid: AppId,
      platform: 'qq'
    })
  };

  let Result = await (await fetch(QueryUrl, QueryOptions)).json() as any;
  if (Result.retcode != '0' || !Result.data) {
    Logger.Error(`Account@GetBotBannedList -> ApiAuth login fail.`);
    return false;
  };

  let Data = Result.data;
  Data.token = Data.minico_token;
  delete Data.minico_token;
  delete Data.expire;

  let Argument = {
    appid: AppId,
    ...Data
  };
  Argument = Object.keys(Argument).map(key =>
    `${key}=${Argument[key]}`
  ).join('&');

  QueryUrl = `https://minico.qq.com/minico/cgiproxy/v3_release/v3/getillegalityhistory?${Argument}`;
  QueryOptions.body = JSON.stringify({
    com: {
      src: 0,
      scene: 1001,
      platform: 2,
      version: `"${Client.apk.version}"`
    }, pageNum: 0, pageSize: Count
  });
  Result = await (await fetch(QueryUrl, QueryOptions)).json();
  if (Result.retcode != '0' || !Result.records) {
    Logger.Error(`Account@GetBotBannedList -> Query records fail.`);
    return false;
  };

  if (Result.totalSize < 1) {
    reply('Haven\'t any banned-record yet. ');
    return true;
  };

  let MsgList: icqq.Forwardable[] = [];
  let Records = Result.records;
  for (let Record of Records) {
    let Violation_info = violation_list.find(val =>
      val.reason = Record.reason
    )!;

    let KeepDays = Record.duration > 0
      ? Math.ceil((Record.duration - Record.time) / 86400) + 'd'
      : '';
    let Reason = Violation_info.reasonDesc == ''
      ? Violation_info.title
      : `Because ${Violation_info.reasonDesc} that got banned ${KeepDays}。`;

    MsgList.push({
      user_id: UserId,
      nickname: '',
      message: [
      `BeginDate：${new Date(Record.time * 1000 + 28800000).toISOString()}`,
      `BanReason：${Reason}`,
      `Description：${Violation_info.description}`
    ].join('\n')
    });
  };

  Client.makeForwardMsg(MsgList).then(text => reply(text));
  return true;
};

